import React, { useState, useEffect } from "react";
import { Col, Image, ListGroup, ListGroupItem, Row } from "react-bootstrap";
import { Link, useParams } from "react-router-dom";

function RestaurantDetails() {
  const [data, setData] = useState([]);
  const params = useParams([]);
  const { id } = params;
  useEffect(() => {
    const fetchData = async () => {
      await fetch("/restaurants.json")
        .then((res) => res.json())
        .then((data) => setData(data.restaurants));
    };
    fetchData();
  }, []);
  const details = data.find((i) => i.id == id);
  return (
    <>
      <Link className="back-btn" to="/">
        Back To Home
      </Link>
      {details ? (
        <div className="deataisl">
        <Row className="my-3">
          <Col md={3}>
            <Image
              className="img"
              src={details.photograph}
              alt={details.name}
            />
          </Col>
          <Col md={4}>
           <ListGroup.Item>
            <h2> {details.name}</h2>
            <p>{details.neighborhood}</p>

           </ListGroup.Item>
           <ListGroup.Item>
           <p>Cuisine{details.cuisine_type}</p>
           </ListGroup.Item>
          </Col>
        </Row>
        </div>
      ) : null}
    </>
  );
}

export default RestaurantDetails;
